import * as React from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import FileDownloadIcon from '@mui/icons-material/FileDownload';
import PropTypes from 'prop-types';
import LinearProgress from '@mui/material/LinearProgress';


export default function TorrentCard({ torrent }) {




    const handleDownload = event => {
        //setIsDone(event.target.value);
    };
    console.log('torrent from Card', torrent)

    function LinearProgressWithLabel(props) {
        return (
            <Box sx={{ display: 'flex', alignItems: 'center' }}>
                <Box sx={{ minWidth: 35 }}>
                    <Typography variant="body2" color="text.secondary">{`${Math.round(
                        props.value,
                    )}%`}</Typography>
                </Box>
                <Box sx={{ width: '100%', mr: 1 }}>
                    <LinearProgress variant="determinate" {...props} />
                </Box>
            </Box>
        );
    }

    const [progress, setProgress] = React.useState(10);
    React.useEffect(() => {
        const timer = setInterval(() => {
            setProgress((prevProgress) => (prevProgress >= 100 ? 10 : prevProgress + 10));
        }, 800);
        return () => {
            clearInterval(timer);
        };
    }, []);


    return (
        <Card sx={{ display: 'flex', padding: 1, marginY: 0.2, marginX: "6%" }}>
            <Box sx={{ display: 'flex', flexDirection: 'row' }}>
                {/*
                <CardMedia
                    component="img"
                    sx={{ width: 51 }}
                    image={suggestion.small_cover_image}
                    alt={suggestion.title}
                />
                */}
                <CardContent sx={{ display: 'flex', flexDirection: 'column' }}>
                    <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                        <Typography component="div" > {torrent.name} </Typography>
                        <Typography variant="subtitle1" color="text.secondary" component="div">
                            peers: {torrent.peersSendingToUs} seeds: {torrent.peersGettingFromUs} Rate: {torrent.rateDownload} eta: {torrent.eta} 
                        </Typography>
                    </Box>
                    <Box sx={{ width: '100%' }}>
                        <LinearProgressWithLabel value={progress} />
                    </Box>
                </CardContent>
            </Box >
        </Card >
    );
}
