import * as React from 'react';
import Box from '@mui/material/Box';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import HighQualityIcon from '@mui/icons-material/HighQuality';
import HdIcon from '@mui/icons-material/Hd'; 
import FileDownloadIcon from '@mui/icons-material/FileDownload';
import FourKIcon from '@mui/icons-material/FourK';
import ThreeDRotationIcon from '@mui/icons-material/ThreeDRotation';
import EightKIcon from '@mui/icons-material/EightK';

export default function SuggestionCard({ suggestion, selectTorrent }) {


    const iconSelector = torrent => {
        switch (torrent.quality) {
            case "720p":
                return <HighQualityIcon sx={{ height: 38, width: 38 }} />
            case "1080p":
                return <HdIcon sx={{ height: 38, width: 38 }} />
            case "3D":
                return <ThreeDRotationIcon sx={{ height: 38, width: 38 }} />
            case "4k":
                return <FourKIcon sx={{ height: 38, width: 38 }} />
            case "2160p":
                return <EightKIcon sx={{ height: 38, width: 38 }} />
            default:
                return <FileDownloadIcon sx={{ height: 38, width: 38 }} />
        }
    }

    return (
        <Card sx={{ display: 'flex' }}>
            <Box sx={{ display: 'flex', flexDirection: 'row' }}>
                <CardMedia
                    component="img"
                    sx={{ width: 51 }}
                    image={suggestion.small_cover_image}
                    alt={suggestion.title}
                />
                <CardContent sx={{ flex: '1 0 auto' }}>
                    <Typography component="div" >
                        {suggestion.title}
                    </Typography>
                    <Typography variant="subtitle1" color="text.secondary" component="div">
                        ({suggestion.year}) - {suggestion.rating} - {suggestion.language} -
                        {suggestion.genres ? suggestion.genres.map(genre => genre + " ") : ""}
                    </Typography>
                </CardContent>
                {/* add imbd icon button link */}
                <Box sx={{ display: 'flex', alignSelf: 'right', alignItems: 'center', pl: 1, pb: 1 }}>
                    {suggestion.torrents.map(torrent =>
                        <IconButton key={torrent.url} aria-label="play/pause" onClick={() => selectTorrent(torrent)}>
                            <Box sx={{ display: 'flex', flexDirection: 'column' }}>
                                {iconSelector(torrent)}
                                <Typography variant="subtitle1" color="text.secondary">
                                    {torrent.type} | {torrent.size}
                                </Typography>
                            </Box>
                        </IconButton>
                    )}
                </Box>
            </Box>
        </Card>
    );
}
